function ConvertDDToDMS(D, lng){
    // from https://stackoverflow.com/a/5786281/2108279
    return {
        dir : D<0?lng?'W':'S':lng?'E':'N',
        deg : 0|(D<0?D=-D:D),
        min : 0|D%1*60,
        sec :(0|D*60%1*6000)/100
    };
}

msg.payload = {
    "lat":ConvertDDToDMS(msg.payload.lat, false),
    "lon":ConvertDDToDMS(msg.payload.lon, true)
};
return msg;

// Format 36°57.4439' N, 110°4.2100' W
// From https://stackoverflow.com/questions/1140189/converting-latitude-and-longitude-to-decimal-values
function ConvertDDMMToDD(input) {
    var parts = input.split(/[^\d\w]+/);
    var dd = Number(parts[0]) + (Number(parts[1]) + Number(parts[2])/10000)/60;

    if (parts[3] == "S" || parts[3] == "W") {
        dd = dd * -1;
    } // Don't do anything for N or E
    return dd.toFixed(6);
}

